---------------------------- Create table ----------------------------------
-- Patients Table
CREATE TABLE Patients (
    PatientID INT PRIMARY KEY,
    FirstName NVARCHAR(50) NOT NULL,
    LastName NVARCHAR(50) NOT NULL,
    DateOfBirth DATE NOT NULL,
    Gender nvarchar(20) CHECK (Gender IN ('Male', 'Female', 'Other')) NOT NULL,
    Email NVARCHAR(100) UNIQUE NOT NULL,
    Phone NVARCHAR(15),
    Address NVARCHAR(255),
    BloodType CHAR(3)
);

-- Doctors Table
CREATE TABLE Doctors (
    DoctorID INT PRIMARY KEY,
    FullName NVARCHAR(100) NOT NULL,
    Specialty NVARCHAR(100) NOT NULL,
    Email NVARCHAR(100) UNIQUE NOT NULL,
    Phone NVARCHAR(15),
    ExperienceYears INT CHECK (ExperienceYears >= 0)
);

-- Appointments Table
CREATE TABLE Appointments (
    AppointmentID INT PRIMARY KEY,
    PatientID INT FOREIGN KEY REFERENCES Patients(PatientID),
    DoctorID INT FOREIGN KEY REFERENCES Doctors(DoctorID),
    AppointmentDate DATETIME NOT NULL,
    Status NVARCHAR(20) CHECK (Status IN ('Scheduled', 'Completed', 'Canceled')) DEFAULT 'Scheduled',
    Notes NVARCHAR(500),
    CreatedAt DATETIME DEFAULT GETDATE()
);

-- Medical Records Table
CREATE TABLE MedicalRecords (
    RecordID INT PRIMARY KEY,
    PatientID INT FOREIGN KEY REFERENCES Patients(PatientID),
    DoctorID INT FOREIGN KEY REFERENCES Doctors(DoctorID),
    AppointmentID INT FOREIGN KEY REFERENCES Appointments(AppointmentID),
    Diagnosis NVARCHAR(255) NOT NULL,
    Treatment NVARCHAR(255),
    Notes NVARCHAR(MAX),
    RecordDate DATETIME DEFAULT GETDATE()
);

-- Prescriptions Table
CREATE TABLE Prescriptions (
    PrescriptionID INT PRIMARY KEY,
    PatientID INT FOREIGN KEY REFERENCES Patients(PatientID),
    DoctorID INT FOREIGN KEY REFERENCES Doctors(DoctorID),
    RecordID INT FOREIGN KEY REFERENCES MedicalRecords(RecordID),
    Medicine NVARCHAR(100) NOT NULL,
    Dosage NVARCHAR(50) NOT NULL,
    Instructions NVARCHAR(255),
    PrescriptionDate DATETIME DEFAULT GETDATE()
);

-- Billing Table
CREATE TABLE Billing (
    BillID INT PRIMARY KEY,
    PatientID INT FOREIGN KEY REFERENCES Patients(PatientID),
    AppointmentID INT FOREIGN KEY REFERENCES Appointments(AppointmentID),
    TotalAmount DECIMAL(10,2) NOT NULL,
    PaidAmount DECIMAL(10,2) DEFAULT 0,
    PaymentStatus NVARCHAR(20) CHECK (PaymentStatus IN ('Pending', 'Paid', 'Overdue')) DEFAULT 'Pending',
    BillDate DATETIME DEFAULT GETDATE()
);

