---------------------------- Create table ----------------------------------
-- Table: Country
CREATE TABLE Country (
    CountryCode varchar(10) PRIMARY KEY,
    CountryName NVARCHAR(100) NOT NULL
);
GO

-- Table: University
CREATE TABLE University (
    UniversityID INT PRIMARY KEY,
    UniversityName NVARCHAR(200) NOT NULL,
    CountryCode varchar(10) NOT NULL,
    EstablishedYear INT,
    Website NVARCHAR(255),
    FOREIGN KEY (CountryCode) REFERENCES Country(CountryCode)
);
GO

-- Table: Faculty/Department
CREATE TABLE Faculty (
    FacultyID INT PRIMARY KEY,
    UniversityID INT NOT NULL,
    FacultyName NVARCHAR(150) NOT NULL,
    FOREIGN KEY (UniversityID) REFERENCES University(UniversityID)
);
GO

-- Table: Program
CREATE TABLE Program (
    ProgramID INT PRIMARY KEY,
    FacultyID INT NOT NULL,
    ProgramName NVARCHAR(150) NOT NULL,
    DegreeType NVARCHAR(50), -- e.g., Bachelor's, Master's, PhD
    DurationYears INT,
    FOREIGN KEY (FacultyID) REFERENCES Faculty(FacultyID)
);
GO

-- Table: Ranking Agency
CREATE TABLE RankingAgency (
    AgencyID INT PRIMARY KEY,
    AgencyName NVARCHAR(150) NOT NULL,
    Website NVARCHAR(255)
);
GO

-- Table: Ranking
CREATE TABLE Ranking (
    RankingID INT PRIMARY KEY,
    UniversityID INT NOT NULL,
    AgencyID INT NOT NULL,
    RankingYear INT NOT NULL,
    Score DECIMAL(5,2), -- e.g., total score from ranking agency
    FOREIGN KEY (UniversityID) REFERENCES University(UniversityID),
    FOREIGN KEY (AgencyID) REFERENCES RankingAgency(AgencyID)
);
GO

-- Table: Ranking Criterion
CREATE TABLE RankingCriterion (
    CriterionID INT PRIMARY KEY,
    AgencyID INT NOT NULL,
    CriterionName NVARCHAR(100) NOT NULL,
    Weight DECIMAL(5,2), -- e.g., 40.00 for 40%
    FOREIGN KEY (AgencyID) REFERENCES RankingAgency(AgencyID)
);
GO

-- Table: Ranking Detail (stores score by criterion)
CREATE TABLE RankingDetail (
    RankingDetailID INT PRIMARY KEY ,
    RankingID INT NOT NULL,
    CriterionID INT NOT NULL,
    Score DECIMAL(5,2), -- e.g., score out of 100
    FOREIGN KEY (RankingID) REFERENCES Ranking(RankingID),
    FOREIGN KEY (CriterionID) REFERENCES RankingCriterion(CriterionID)
);
GO

